mapper.select:
SELECT EMP_CODE , COMPANY_CODE , COMPANY_NAME , EMP_NAME , EMP_ENG_NAME , HIRE_DATE , RETIREMENT_DATE , USER_OR_NOT , SOCIAL_SECURITY_NUMBER , BIRTH_DATE , GENDER , SEQ , UPDATE_HISTORY , UPDATE_DATE , USER_ID , WORKPLACE_CODE , WORKPLACE_NAME , DEPT_CODE , PHONE_NUMBER , EMAIL , ZIP_CODE , BASIC_ADDRESS , DETAIL_ADDRESS , LEVEL_OF_EDUCATION , IMAGE , POSITION_CODE , POSITION_NAME , DEPT_NAME ,AUTHORITY_CODE FROM (SELECT E1.EMP_CODE , E1.COMPANY_CODE , C.COMPANY_NAME , E1.EMP_NAME , E1.EMP_ENG_NAME , E1.HIRE_DATE , E1.RETIREMENT_DATE , E1.USER_OR_NOT , E1.SOCIAL_SECURITY_NUMBER , E1.BIRTH_DATE , E1.GENDER , E2.EMAIL , E2.SEQ , E2.UPDATE_HISTORY , E2.UPDATE_DATE , E2.USER_ID , E2.WORKPLACE_CODE , W.WORKPLACE_NAME , E2.DEPT_CODE , E2.PHONE_NUMBER , E2.ZIP_CODE , E2.BASIC_ADDRESS , E2.DETAIL_ADDRESS , E2.LEVEL_OF_EDUCATION , E2.IMAGE , E2.POSITION_CODE , E2.AUTHORITY_CODE , P.POSITION_NAME , D.DEPT_NAME FROM EMPLOYEE_BASIC E1 , EMPLOYEE_DETAIL E2 , WORKPLACE W , POSITION P , DEPARTMENT D , COMPANY C WHERE E1.EMP_CODE = E2.EMP_CODE AND E1.COMPANY_CODE = W.COMPANY_CODE AND E2.WORKPLACE_CODE = W.WORKPLACE_CODE AND E2.WORKPLACE_CODE = P.WORKPLACE_CODE AND E2.DEPT_CODE = P.DEPT_CODE AND E2.POSITION_CODE = P.POSITION_CODE AND E2.WORKPLACE_CODE = D.WORKPLACE_CODE AND E2.DEPT_CODE = D.DEPT_CODE AND E2.COMPANY_CODE= C.COMPANY_CODE AND E2.POSITION_CODE = P.POSITION_CODE AND ( E2.EMP_CODE , E2.SEQ ) IN (SELECT EMP_CODE , MAX(SEQ) FROM EMPLOYEE_DETAIL GROUP BY EMP_CODE ) ) WHERE COMPANY_NAME = #{company}
AND WORKPLACE_NAME = #{workPlace}
AND POSITION_NAME= #{position}
AND EMP_NAME = #{empName}
ORDER BY EMP_CODE
